sys_variable_value.value:
(function execute(inputs, outputs) {

  var importSetRunId = inputs.importSetRunSysId;
  var importSetRun = new SGCEnhanceTransformHistory(importSetRunId);
  importSetRun.rollback();
  var transformHistoryGr = new GlideRecord('sys_import_set_run');
    if(transformHistoryGr.get(importSetRunId)){
      new SGCEnhanceNotification().postSimpleNotificationToUser(gs.getUserID(), transformHistoryGr, transformHistoryGr.getDisplayValue(), "The transform has been rolled back.", "Reverted");
    }

})(inputs, outputs);

