	// deleteRecord: function(){
	// 	var transformHistoryGr = new GlideRecord("sys_import_set_run");
	// 	if(transformHistoryGr.get(this.sysId)){
	// 		transformHistoryGr.deleteRecord();
	// 	}
	// },

	//Internal Functions

	_getReferenceField: function(columnName, transformHistoryGr){
		var returnObj = {};
		if(columnName){
			returnObj.id = transformHistoryGr.getValue(columnName);
			returnObj.displayValue = transformHistoryGr.getDisplayValue(columnName);
		}
		return returnObj;
	},

	// _assignGlideRecordValues: function(transformHistoryGr){
    //     transformHistoryGr.sys_created_on = this.sysCreatedOn;
	// 	transformHistoryGr.completed = this.completed;
	// 	transformHistoryGr.run_time = this.runTime;
	// 	transformHistoryGr.set = this.set.sysId;
