    _getGlideRecordWithId: function(id){
        var transformHistoryGr = new GlideRecord("sys_import_set_run");
        if(transformHistoryGr.get(id)){
            return transformHistoryGr;
        }
    },

    type: 'SGCEnhanceTransformHistory'
};
